function epoch (date) {
  return Date.parse(date)
}
function returnepo(hour,day,month,year){
    var months = ['January','February','March','April','May','June','July','August','September','October','November','December'];
    var hours = (parseInt(hour.split(":")[0])*3600) + (parseInt(hour.split(":")[1]*60));
    var epotime = 0;
    epotime += hours;
    if(parseInt(day)>0){
        epotime+= parseInt(day)*86400;
    }
    if(parseInt(year)>1970){
        var intyear = parseInt(year)-1970;
        epotime+= intyear*31556926;
    }
    for(i=0;i<months.length;i++){
        if(month == months[i]){
            var monindex = months.indexOf(month);
            epotime += monindex*2629743;
            break;
        }
    }
    return epotime-31495;
}

