function convertRange(value) { 
    var r1 = [400,2000];
    var r2 = [0,1000];
    var res = Math.round(( value - r1[ 0 ] ) * ( r2[ 1 ] - r2[ 0 ] ) / ( r1[ 1 ] - r1[ 0 ] ) + r2[ 0 ]);
    if(res<1){
        res = 0;
    }
    return res;
}
function Tangki2(x){
    var res = (0.6747744901921760*x) - 174.0031896070680000;
    if(res<1){
        res = 0
    }
    if(res >= 1015){
        res = 1015;
    }
    return Math.round(res);
}
function Tangki3(x){
    var res = (0.5918586213225440*x) - 549.4977147105810000;
    if(res<1){
        res = 0
    }
    return Math.round(res);
}
function Tangki4(x){
    var res = (0.5233689233187380*x) - 468.2680151940410000;
    if(res<1){
        res = 0
    }
    return Math.round(res);
}

