function geofenceID(data2,data1){
    var res = [];
    try{
        var pathName = data2;
        var pN = {};
        var num = 1;
        pN[String(num)] = [];
        for(i=0;i<pathName.length;i++){
            if(num == pathName[i].PathID){
                pN[String(num)].push({"PathNum":pathName[i].PathNum,"geofenceID":pathName[i].geofenceID,"Main_Activity":pathName[i].Main_Activity});
            }else{
                num = num + 1;
                pN[String(num)] = [];
                pN[String(num)].push({"PathNum":pathName[i].PathNum,"geofenceID":pathName[i].geofenceID,"Main_Activity":pathName[i].Main_Activity});
            }
        }
        for(i=0;i<pN[data1.PathID].length;i++){
            if(data1.PathNum == pN[data1.PathID][i].PathNum){
                res.push(pN[data1.PathID][i].geofenceID);
                res.push(pN[data1.PathID][i].Main_Activity);
                if(data1.PathNum == pN[data1.PathID].length){
                    res.push(pN[data1.PathID][0].Main_Activity);
                }else{
                    res.push(pN[data1.PathID][i+1].Main_Activity);
                }

            }
        }
    }
    catch(a){
        var geonow = flow.get("PositionNow").geoID;
        res.push(geonow);
        res.push("");
        res.push("");
    }
    return res;
}

