function RPMKANAN(x){
    var res = (0.6*x) - 340.66;
    if(res<1){
        res = 0;
    }else if(res<70){
        res = 0;
    }
    return parseFloat(res.toFixed(2));
}

function RPMKIRI(x){
    var res = (0.6335*x) - 436.26;
    if(res<1){
        res = 0;
    }else if(res<70){
        res = 0;
    }
    return parseFloat(res.toFixed(2));
}

function convertws(x) {
    var val = parseInt(String(x));
    var r1 = [0, 5000];
    var r2 = [0, 30];
    var sp = (val - r1[0]) * (r2[1] - r2[0]) / (r1[1] - r1[0]) + r2[0];
    var res = parseFloat((sp * 1.94384).toFixed(2));
    return res;
}

