function distance(lat1, lon1, lat2, lon2, unit) {
	if ((lat1 == lat2) && (lon1 == lon2)) {
		return 0;
	}
	else {
		var radlat1 = Math.PI * lat1/180;
		var radlat2 = Math.PI * lat2/180;
		var theta = lon1-lon2;
		var radtheta = Math.PI * theta/180;
		var dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
		if (dist > 1) {
			dist = 1;
		}
		dist = Math.acos(dist);
		dist = dist * 180/Math.PI;
		dist = dist * 60 * 1.1515;
		if (unit=="K") { dist = dist * 1.609344 }
		if (unit=="N") { dist = dist * 0.8684 }
		return dist;
	}
}
function angle(cx, cy, ex, ey) {
  var dy = ey - cy;
  var dx = ex - cx;
  var theta = Math.atan2(dy, dx);
  theta *= 180 / Math.PI; 
  if(theta<0){
      theta = 360+theta;
  }
  return theta.toFixed(2);
}

function isEmpty(obj) {
  for(var prop in obj) {
    if(Object.prototype.hasOwnProperty.call(obj, prop)) {
      return false;
    }
  }
  return JSON.stringify(obj) === JSON.stringify({});
}

function formatdate(x){
    var val = parseInt(String(x));
    var full = String(new Date(x));
    var day = String(full.split(" ")[2]);
    var month = ""
    if (getMonthFromString(full.split(" ")[1]) < 10){
        month += "0";
    }
    month += String(getMonthFromString(full.split(" ")[1]));
    var year = String(full.split(" ")[3]);
    var time = String(full.split(" ")[4].substring(0,5));
    return day+"/"+month+"/"+year+" "+time;

    function getMonthFromString(mon){
       return new Date(Date.parse(mon +" 1, 2012")).getMonth()+1
    }
}

