    var n = flow.get("avr"+String(b));
    if(a != "-1"){
        if(b<8){
            a = parseInt(String(a)); // 800-4000 -> 400-2000
        }else if(b>=7){
            a = parseInt(String(a));
        }
        var cekk = isNaN(a);
        if(!cekk){
            n.push(a);    
        }

        if(n.length>10){
            n.shift();
        }
        flow.set("avr"+String(b),n);
    }
    if(b<8){
        if(avr(n)<=400){
            return 400;
        }else{
            return avr(n);
        }
    }else{
        return avr(n);
    }
}
