function detectInt(x){
    try{
        if(x=="-1"){
            x="0";
        }
        return parseInt(x);
    }catch(s){
        return 0;
    }
}

function avr(n){
    var rs = 0;
    for(i=0;i<n.length;i++){
        rs+=n[i];
    }
    rs = Math.round(rs/n.length);
    return rs;
}

function MA(a,b){
    var n = flow.get("avr"+String(b));
    if(b==9 || b == 10){
        if(a<10){
            a = 0;
        }
    }
    if(a < 100){
        if(avr(n)<=400){
            return avr(n);
        }else{
            return avr(n);
        }
    }
    if(a != "-1"){
        if(b<7){
            a = parseInt(String(a)); // 800-4000 -> 400-2000
        }else if(b>=7){
            a = parseInt(String(a));
        }
        var cekk = isNaN(a);
        if(!cekk){
            n.push(a);    
        }
        if(n.length>10){
            n.shift();
        }
        flow.set("avr"+String(b),n);
    }
    if(b<7){
        if(avr(n)<=400){
            if(avr(n)>=200){
                return 400;
            }
        }else{
            return avr(n);
        }
    }else{
        return avr(n);
    }
}

