    return (dpn * -0.0715895679807236)+(blg * 0.2189957537736704)+(54.37558133121867)	
}
function rumuskanan10double(dpn,blg){
    return (dpn * 0.06983610340529135)+(blg * 0.09880988417745153)+(2.072062588656962)	
}

