function RPMKANAN(x){
    var res = (0.4909 * x) - 7.1141;
    if(res<1){
        res = 0;
    }else if(res<70){
        res = 0;
    }
    return res;
}

function RPMKIRI(x){
    var res = (0.4126 * x) - 14.878;
    if(res<1){
        res = 0;
    }else if(res<70){
        res = 0;
    }
    return res;
}

function PropKanan(x){
    var res = (0.2376*x)+21.024;
    if(res<40 || x<70){
        res = 0;
    }
    return res;
}
function PropKiri(x){
    var res = (0.2373*x)+0.0967;
    if(res<40 || x<70){
        res = 0;
    }
    return res;
}
function convertws(x) {
    var val = parseInt(String(x));
    var r1 = [0, 5000];
    var r2 = [0, 30];
    var sp = (val - r1[0]) * (r2[1] - r2[0]) / (r1[1] - r1[0]) + r2[0];
    var res = parseFloat((sp * 1.94384).toFixed(2));
    return res;
}

