function convertRange(value) { 
    if(value>2000){
        value = 2000;
    }else if(value<400){
        value = 400;
    }
    var r1 = [400,2000];
    var r2 = [0,1000];
    return Math.round(( value - r1[ 0 ] ) * ( r2[ 1 ] - r2[ 0 ] ) / ( r1[ 1 ] - r1[ 0 ] ) + r2[ 0 ]);
}
function convertRange2(value) { 
    var r1 = [400,2000];
    var r2 = [0,100];
    return Math.round(( value - r1[ 0 ] ) * ( r2[ 1 ] - r2[ 0 ] ) / ( r1[ 1 ] - r1[ 0 ] ) + r2[ 0 ]);
}

function density(value) { 
    if(value == null || value < 400){
        value = 400;
    }else if(value > 2000){
        value = 2000;
    }
    var r1 = [400,2000];
    var r2 = [0,2];
    return parseFloat((( value - r1[ 0 ] ) * ( r2[ 1 ] - r2[ 0 ] ) / ( r1[ 1 ] - r1[ 0 ] ) + r2[ 0 ]).toFixed(3));
}

