function gm(mon){
   var d = Date.parse(mon + "1, 2012");
   if(!isNaN(d)){
       var res = "";
       if(String(new Date(d).getMonth() + 1).length<2){
           res = "0" + String(new Date(d).getMonth() + 1);
       }else{
           res = String(new Date(d).getMonth() + 1);
       }
      return res;
   }
   return -1;
 }

