function simpledate(x){
    var spt = x.split(' ');
    var str = "";
    str+=spt[2]+"/";
    str+=String(gmfs(spt[1]))+"/";
    str+=spt[3]+" ";
    str+=spt[4].substring(0,5);
    return str;
}

function gmfs(mon){
   var d = Date.parse(mon + "1, 2012");
   if(!isNaN(d)){
      return new Date(d).getMonth() + 1;
   }
   return -1;
}

function randomp(x){
    var number = flow.get("set"+String(x));
    var plusOrMinus = Math.random() < 0.5 ? -1 : 1;
    number = Math.abs(number + (Math.round(Math.random()*100)));
    number = Math.abs(number);
    flow.set("set"+String(x),number);
    return String(number);
}
function randomm(x){
    var number = flow.get("set"+String(x));
    var plusOrMinus = Math.random() < 0.5 ? -1 : 1;
    number = Math.abs(number - (Math.round(Math.random()*100)));
    number = Math.abs(number);
    flow.set("set"+String(x),number);
    return String(number);
}
function random(x){
    var number = flow.get("set"+String(x));
    var plusOrMinus = Math.random() < 0.5 ? -1 : 1;
    if(plusOrMinus <0){
        number = Math.abs(number - (Math.round(Math.random()*10)));
        number = Math.abs(number);
    }else if(plusOrMinus >= 0){
        number = Math.abs(number + (Math.round(Math.random()*10)));
    }
    flow.set("set"+String(x),number);
    return String(number);
}

function randomd(x){
    var number = flow.get("setd"+String(x));
    var plusOrMinus = Math.random() < 0.5 ? -1 : 1;
    if(plusOrMinus <0 || number >= 2){
        number = Math.abs(number - (Math.random()/100));
        number = Math.abs(number);
        if(number < 0){
            number = 0;
        }
    }
    if(plusOrMinus >= 0 || number <= 0){
        number = Math.abs(number + (Math.random()/100));
        if(number > 2){
            number = 2;
        }
    }
    flow.set("setd"+String(x),number);
    return String(number);
}
function randomt(x){
    var number = flow.get("sett"+String(x));
    var plusOrMinus = Math.random() < 0.5 ? -1 : 1;
    if(plusOrMinus <0 || number >= 43){
        number = Math.abs(number - (Math.round(Math.random())));
        number = Math.abs(number);
    }
    if(plusOrMinus >= 0|| number <= 25){
        number = Math.abs(number + (Math.round(Math.random())));
    }
    flow.set("sett"+String(x),number);
    return String(number);
}

