function ConvertToTemp(value){
    var r1 = [400,2000];
    var r2 = [0,100];
    var res =  parseFloat((( value - r1[ 0 ] ) * ( r2[ 1 ] - r2[ 0 ] ) / ( r1[ 1 ] - r1[ 0 ] ) + r2[ 0 ]).toFixed(0));
    if(res<=0){
        res = 0;
    }else if(res>=100){
        res = 100;
    }
    return res;
}

function tank15deg(x,y){
    var k0less770 = 346.42278;
    var k0around778839 = 594.5418;
    var k0more839 = 186.9696;
    var k1less770 = 0.43884;
    var k1around778839 = 0;
    var k1more839 = 0.48618;
    var alpha = 0.0;
    if(x<=770){
        alpha = createalpha(k0less770,k1less770,x);
    }else if(x>=778 && x<839){
        alpha = createalpha(k0around778839,k1around778839,x);
    }else if(x>=839){
        alpha = createalpha(k0more839,k1more839,x);
    }
    json['alpha'] = alpha;
    var DegC = y-15;
    return parseFloat(Math.pow(2.71828,((alpha*-1)*DegC*(1+0.8*alpha*DegC))).toFixed(4));
}

function createalpha(x,y,z){
    return (x+(y*z))/Math.pow(z,2)
}

function simpledate(x){
    var spt = x.split(' ');
    var str = "";
    str+=spt[2]+"/";
    str+=String(gmfs(spt[1]))+"/";
    str+=spt[3]+" ";
    str+=spt[4].substring(0,5);
    return str;
}

function gmfs(mon){
   var d = Date.parse(mon + "1, 2012");
   if(!isNaN(d)){
      return new Date(d).getMonth() + 1;
   }
   return -1;
}

function map_range(value, low1, high1, low2, high2) {
    var res = low2 + (high2 - low2) * (value - low1) / (high1 - low1);
    if(res<0){
        return 0;
    }else{
        return parseFloat((res).toFixed(3));
    }
}

