function avr(n){
    var rs = 0;
    for(i=0;i<n.length;i++){
        rs+=n[i];
    }
    rs = Math.round(rs/n.length);
    return rs;
}

function MA(a,b){
    var n = flow.get("avr"+String(b));
    if(a != "-1"){
        if(b<8){
            a = parseInt(String(a)); // 800-4000 -> 400-2000
        }else if(b>=8){
            a = parseFloat(String(a));
        }
        if(a % 1 === 0){
            n.push(a);
        }
        if(n.length>10){
            n.shift();
        }
        flow.set("avr"+String(b),n);
    }
    if(b<7){
        if(avr(n)<=400){
            return 400;
        }else{
            return avr(n);
        }
    }else{
        return avr(n);
    }
}
function StorageCenter(x,y){
    var res = (x * 0.42841243648887317)+(y * -0.2056183258097715)+(-103.94678576592575);
