function convertrpm( value ) { 
    var r1 = [0,3000];
    var r2 = [0,1500];
    return ( value - r1[ 0 ] ) * ( r2[ 1 ] - r2[ 0 ] ) / ( r1[ 1 ] - r1[ 0 ] ) + r2[ 0 ];
}

function convertws(x) {
    var val = parseInt(String(x));
    var r1 = [0, 5000];
    var r2 = [0, 30];
    var sp = (val - r1[0]) * (r2[1] - r2[0]) / (r1[1] - r1[0]) + r2[0];
    var res = parseFloat((sp * 1.94384).toFixed(2));
    return res;
}

