function InsertData(table){
    var alldata = msg.alldata;
    var popupp = msg.popupp;
    msg.topic = ""
    var column = ['EPO', 'SHIP_NAME', 'BARGE', 'DEPARTURE', 'ARRIVAL', 'CURRENTT', 'NEXTT', 'TD', 'ETA', 'LASTRECORDDATE', 'LASTRECEIVEDATE', 'LATITUDE', 'LONGITUDE', 'FROMM', 'DESTINATION', 'STATUS', 'ANGLE', 'ROB', 'ROBONSTART', 'ESTROB', 'FUELCONS', 'ST', 'RPMENGINE_S', 'RPMENGINE_P', 'RPMAVR', 'RPMPROPELLER_S', 'RPMPROPELLER_P', 'WINDSPEED', 'SPEED', 'TARGET_FO'];
    msg.topic += "INSERT INTO `"+String(table)+"`("; // VMSDATA
    for(i=0;i<column.length;i++){
        msg.topic+="`";
        msg.topic+=column[i];
        msg.topic+="`";
        if(i<(column.length-1)){
            msg.topic += ",";
        }
    }
    msg.topic += ") VALUES (";
    for(i=0;i<column.length;i++){
        var res = ""
        var fromdata = {}
        if(alldata.hasOwnProperty(column[i])){
            fromdata = alldata;
        }else if(popupp.hasOwnProperty(column[i])){
            fromdata = popupp;
        }
        if(column[i]=="ANGLE"){
            res = parseFloat(String(msg.data.Angle));
        }else if(column[i]=="STATUS"){
            var stat = Date.now()-msg.data.Time;
            if(stat > 432000000){ // five days
                res = "2";
            }else if(stat > 172800000){ // 2 days
                res = "1";
            }else{  // online
                res = "0";
            }
        }else if(column[i]=="ST"){
            res = secondsToDhms((Date.now()-flow.get("ONSTART").time)/1000);
            if(res==""){
                res = "_";
            }
        }else if(column[i]=="SPEED"){
            res = String(msg.speed);
        }else if(column[i]=="ESTROB"){
            res = String(parseInt(String(flow.get("ONSTART").ROB))-parseInt(String(popupp.TARGET_FO)));
        }else if(column[i]=="FUELCONS"){
            res = String(parseInt(String(flow.get("ONSTART").ROB))-parseInt(String(msg.ROB)));
        }else if(column[i]=="EPO" || column[i]=="LATITUDE" || column[i]=="LONGITUDE"){
            res = fromdata[column[i]];    
        }else{
            res = String(fromdata[column[i]]);    
        }

        if (typeof res === 'string' || res instanceof String){
            msg.topic+="'"+res+"'";
        }else{
            msg.topic+=String(res);
        }
        if(i<(column.length-1)){
            msg.topic += ",";
        }
    }
    msg.topic += ");"
}

function secondsToDhms(seconds) {
    seconds = Number(seconds);
    var d = Math.floor(seconds / (3600 * 24));
    var h = Math.floor(seconds % (3600 * 24) / 3600);
    var m = Math.floor(seconds % 3600 / 60);
    var s = Math.floor(seconds % 60);

    var dDisplay = d > 0 ? d + (d == 1 ? " day. " : " days. ") : "";
    var hDisplay = h > 0 ? h + (h == 1 ? " hour. " : " hours. ") : "";
    var mDisplay = m > 0 ? m + (m == 1 ? " minute" : " minutes") : "";
    return dDisplay + hDisplay + mDisplay ;
}

