function detectInt(x){
    try{
        return parseInt(x);
    }catch(s){
        return 0;
    }
}

function avr(n){
    var rs = 0;
    for(i=0;i<n.length;i++){
        rs+=n[i];
    }
    rs = Math.round(rs/n.length);
    return rs;
}

function MA(a,b){
    var n = flow.get("avr"+String(b));
    if(a != "-1"){
        if(b<7){
            a = parseInt(String(a)); // 800-4000 -> 400-2000
        }else if(b>=7){
            a = parseInt(String(a));
        }
        if(a % 1 === 0){
            n.push(a);
        }
        if(n.length>10){
            n.shift();
        }
        flow.set("avr"+String(b),n);
    }
    if(b<7){
        if(avr(n)<=0){
            return 0;
        }else{
            return avr(n);
        }
    }else{
        return avr(n);
    }
}

function RoundCm(x){
    var res = Math.round(x);
    if(res<1){
        res = 0;
